export function Other() {

    const [products, setproducts] = useState([])
    const [collection, setcollection] = useState([])
    useEffect(() => {

        async function fetchProducts() {
            const { data } = await axios.get('http://127.0.0.1:8000/store/products')
            setproducts(data)
            console.log(data)
        }

        fetchProducts()

        async function fetchCollection() {
            const { data } = await axios.get('http://127.0.0.1:8000/store/collections')
            setcollection(data)
            console.log(data)
        }

        fetchCollection()
    }, [])



    const listItems1 = products.map((item) => {
        if (item.collection == 10)

            return (
                <Link to={`/products/${item.id}`} >
                    <div className="card" key={item.id}>

                        <div className="card_img">
                            <img src={'http://127.0.0.1:8000' + item.image} />
                        </div>
                        <div className="card_header">
                            <h2>{item.title}</h2>
                            <p>{item.description}</p>
                            <p className="price">{item.budget}<span></span></p>
                            <div className="btn">Show Details</div>
                        </div>
                    </div>
                </Link>
            )
    }

    );


    return (

        <PageContainer>
            
                <Navbar />
            <div className='container'>
                <h1 style={{"color": "#fff"}}>
                    Others
                </h1>
                <div className="main_content">
                    {listItems1}
                </div>
               
                <Footer>
                </Footer>
            </div>

        </PageContainer>

    )
}